cypher:
CALL {
  MATCH (ip:IP)-[:FROM]->(src:Country {name:'Russia'})
  RETURN count(DISTINCT ip) AS distinctIPs
}
CALL {
  MATCH (ip:IP)-[:FROM]->(src:Country {name:'Russia'})
  MATCH (ip)-[:ATTACKS]->(:Country)
  RETURN count(*) AS totalAttacks
}
CALL {
  MATCH (ip:IP)-[:FROM]->(src:Country {name:'Russia'})
  MATCH (ip)-[a:ATTACKS]->(:Country)
  WITH a.industry AS industry, count(*) AS attacks
  ORDER BY attacks DESC
  LIMIT 5
  RETURN collect({industry: industry, attacks: attacks}) AS topIndustries
}
RETURN distinctIPs, totalAttacks, topIndustries
